import React, { useState, useEffect } from "react";
import { FaBell } from "react-icons/fa";
import "./NotificationBell.css";

function NotificationBell({ predictions }) {
  const [isOpen, setIsOpen] = useState(T);

  // Toggle dropdown visibility
  const toggleDropdown = () => setIsOpen(!isOpen);

  // Show alert count if predictions are available
  const alertCount = predictions.length;

  return (
    <div className="notification-bell">
      <FaBell className="bell-icon" onClick={toggleDropdown} />
      {alertCount > 0 && <span className="alert-count">{alertCount}</span>}
      
      {isOpen && (
        <div className="dropdown-menu">
          <h5>Potential Wastage Alerts</h5>
          {predictions.length > 0 ? (
            <ul>
              {predictions.map((item, index) => (
                <li key={index}>
                  {item.name} - {item.daysToExpiry} day(s) left
                </li>
              ))}
            </ul>
          ) : (
            <p>No potential wastage detected.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default NotificationBell;
