// src/components/FloatingFoodBackground.js
import React from "react";
import "./FloatingFoodBackground.css";

// Array of food emoji icons
const foodIcons = ["🍎", "🍌", "🍉", "🍇", "🍕", "🍟", "🍔", "🥑", "🍅"];

// Function to get random position and size
const getRandomStyle = () => ({
  top: `${Math.floor(Math.random() * 100)}vh`,
  left: `${Math.floor(Math.random() * 100)}vw`,
  fontSize: `${Math.floor(Math.random() * (4 - 2) + 2)}rem`, // Random font size between 2rem and 4rem
});

const FloatingFoodBackground = () => {
  return (
    <div className="floating-food-background">
      {foodIcons.slice(0, 9).map((food, index) => (
        <div
          key={index}
          className="floating-food"
          style={{ ...getRandomStyle(), animationDelay: `${index * 1}s` }}
        >
          {food}
        </div>
      ))}
    </div>
  );
};

export default FloatingFoodBackground;
