import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import Dashboard from "./components/Dashboard";
import MealPlanner from "./components/MealPlanner";
import SurplusManagement from "./components/SurplusManagement";
import InventoryManagement from "./components/InventoryManagement";
import NotificationBell from "./components/NotificationBell";

function App() {
  // Mock data to simulate ML model output for potential wastage
  const [predictions, setPredictions] = useState([
    { name: "Potatoes", daysToExpiry: 3 },
    { name: "Milk", daysToExpiry: 2 },
    { name: "Bread", daysToExpiry: 1 },
  ]);

  return (
    <Router>
      <div className="container">
        <header className="d-flex justify-content-between align-items-center my-4">
          <h1 className="text-center" style={{ color: "var(--dark-bg)" }}>Food Waste Reduction Platform</h1>
          <NotificationBell predictions={predictions} />
        </header>

        <nav className="navbar navbar-expand-lg navbar-light bg-light mb-4">
          <ul className="navbar-nav mx-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/">Dashboard</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/meal-planner">Meal Planner</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/surplus-management">Surplus Management</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/inventory-management">Inventory Management</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/meal-planner" element={<MealPlanner />} />
          <Route path="/surplus-management" element={<SurplusManagement />} />
          <Route path="/inventory-management" element={<InventoryManagement />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
